mapper.select:
SELECT 

FROM mod_log a

WHERE a.id = #{id}

SELECT
FROM mod_log a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.log_type = #{logType}
AND a.entity_class LIKE '%'||#{entityClass}||'%' '%'+#{entityClass}+'%' concat('%',#{entityClass},'%')
AND a.bis_method LIKE '%'||#{bisMethod}||'%' '%'+#{bisMethod}+'%' concat('%',#{bisMethod},'%')
AND a.create_time BETWEEN #{beginCreateTime} AND #{endCreateTime}
ORDER BY ${page.orderBy}

SELECT 

FROM mod_log a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE mod_log SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
